import { createSelector } from 'reselect'

const COLLECTION_ID_MAP = {
  hat: 1,
  sneakers: 2,
  jackets: 3,
  womans: 4,
  mens: 5,
}

const selectShop = (state) => state.shop

export const selectShopCollections = createSelector(
  [selectShop],
  (shop) => shop.collections
)

export const selectShopCollection = (collectionUrlParam) =>
  createSelector([selectShopCollections], (collections) => {
    console.log(collections, 'collections')
    console.log('COLLECTION_ID_MAP', COLLECTION_ID_MAP[collectionUrlParam])
    return collections.find((collection) => {

      console.log('collection.id', collection.id)
      console.log(
        'COLLECTION_ID_MAP[collectionUrlParam]',
        COLLECTION_ID_MAP[collectionUrlParam]
      )
      const result = collection.id === COLLECTION_ID_MAP[collectionUrlParam]
      console.log('collection in selector', result)
      return result
    })
  })
