import { ADD_ITEM, TOGGLE_CART_HIDDEN } from './cart.constants'

const INITIAL_STATE = {
  hidden: true,
  cartItems: [],
}

const cartReducer = (state = INITIAL_STATE, action) => {
  const { type, payload } = action

  switch (type) {
    case TOGGLE_CART_HIDDEN:
      return {
        ...state,
        hidden: !state.hidden,
      }
    case ADD_ITEM: 
      return {
        ...state,
        cartItems: [...state, ]
      }
    default:
      return state
  }
}

export default cartReducer
